int mov,count = 0
s.split("").each{
  if(it == "(") {
    mov += 1
  }else if (it == ")") {
    mov -= 1
  }
  if (mov == -1 ){
    println "Position of the first character that causes Santa to enter the basement ${count}"
  }
  count++
}
